var api_server = "https://dcar.xyz/api"

var token = localStorage.getItem('token');
var currentUser;

// handle if user login or not
if( token!= null &&  token!= "" ) {

    var jqxhr = $.ajax({
        type: "get",
        url:  api_server +  '/profile',
        data: {
            token: token
        }
    })

    // 添加成功，重新加载数据
    jqxhr.done(function(data){
        currentUser = data;
        $("#username").text(currentUser.username);
        $("#li_signin").css("display", "none");
        $("#li_profile").css("display", "inline");
        
    })
    
    jqxhr.fail(function(error){
        $("#li_signin").css("display", "inline");
        $("#li_profile").css("display", "none");
    })

    
} else {
    $("#li_signin").css("display", "inline");
    $("#li_profile").css("display", "none");
}


